    static func seedExercises(context: NSManagedObjectContext) {
        let request: NSFetchRequest<Exercise> = Exercise.fetchRequest()
        
        do {
            let existingCount = try context.count(for: request)
            if existingCount == 0 {
                for exerciseData in exercisesFromCSV {
                    let exercise = Exercise(context: context)
                    exercise.name = exerciseData.name
                    exercise.type = exerciseData.category.capitalized
                    exercise.targetMuscle = mapCategoryToMuscleGroup(exerciseData.category)
                    exercise.instructions = exerciseData.description
                    exercise.imageUrl = exerciseData.imageUrl
                }
                try context.save()
                print("âœ… Seeded \(exercisesFromCSV.count) exercises successfully!")
            } else {
                print("ðŸ“‹ Exercises already exist in database (\(existingCount) found)")
            }
        } catch {
            print("âŒ Failed to seed exercises: \(error)")
        }
    }
    
    private static func mapCategoryToMuscleGroup(_ category: String) -> String {
        switch category.lowercased() {
        case "core":
            return "Core"
        case "chest":
            return "Petto"
        case "fullbody":
            return "Full Body"
        case "back":
            return "Schiena"
        case "shoulders":
            return "Spalle"
        case "arms":
            return "Braccia"
        case "legs":
            return "Gambe"
        case "cardio":
            return "Cardio"
        default:
            return "Generale"
        }
    }
